T_BOOL GetMainMenuText(T_eREC_MENU_ID ID, T_U16 text[eMAIN_ICON_MAX_NUM][ICONMENU_TEXT_LEN+1])
{
    T_U8 i;

    if ((ID != mnMAIN_MENU) || (text == AK_NULL))
        return AK_FALSE;

    for (i=0; i<eMAIN_ICON_MAX_NUM; i++)
        Utl_UStrCpy(text[i], Res_GetStringByID(strID[i]));

    return AK_TRUE;
}

T_BOOL ResetMainMenuText(T_eREC_MENU_ID ID, T_ICONMENU *pIconMenu)
{
    T_U8 i;
    T_U16 text[eMAIN_ICON_MAX_NUM][ICONMENU_TEXT_LEN+1] = {0};

    if ((ID != mnMAIN_MENU) || (pIconMenu == AK_NULL))
        return AK_FALSE;

    GetMainMenuText(ID, text);
    for (i=0; i<eMAIN_ICON_MAX_NUM; i++)
        IconMenu_ResetItemText(pIconMenu, i, text[i]);

    return AK_TRUE;
}

T_BOOL GetMainMenuContent(T_eREC_MENU_ID ID, T_ICONMENU *pIconMenu)
{
    T_U8      *pIconImg;
    T_U32     imgLen;
    T_U8      i, j;
    T_U16 text[eMAIN_ICON_MAX_NUM][ICONMENU_TEXT_LEN+1] = {0};

    if ((ID != mnMAIN_MENU) || (pIconMenu == AK_NULL))
        return AK_FALSE;

    GetMainMenuText(ID, text);

    for (i=0; i<eMAIN_ICON_MAX_NUM; i++) {
        IconMenu_AddItem(pIconMenu, (T_U8)ItemID[i], i, text[i]);
        for (j=0; j<ICONMENU_ICON_NUM; j++)
        {
            pIconImg = (T_U8 *)Res_GetBinResByID(AK_NULL, AK_TRUE, (iconIDs[i] + j), &imgLen);
            if (pIconImg != AK_NULL)
                IconMenu_SetItemIcon(pIconMenu, (T_U8)ItemID[i], j, pIconImg);
        }
    }
	
    return AK_TRUE;
}

